function send(e){browser.tabs.query({active:!0,currentWindow:!0}).then((n=>{browser.tabs.sendMessage(n[0].id,{action:e})}))}browser.runtime.onMessage.addListener((e=>{"checkLinks"===e.action&&send("checkLinks"),"checkImages"===e.action&&send("checkImages"),"outlines"===e.action&&send("outlines"),"getAdobeIds"===e.action&&send("getAdobeIds"),"detectOverflow"===e.action&&send("detectOverflow")}));